import React, { useState, useEffect } from "react";
import Places from "./Places";
import PostServis from "./PostService";


const WeatherDisplay = (props) => {
  const [currentPlace, setPlace] = useState(props);
  const [weatherData, setWeatherData] = useState(null);

  function newPlace(newPlace) {
    setPlace(newPlace);
  }

  useEffect(() => {

    const zip = currentPlace.zip;
    const URL = "http://api.openweathermap.org/data/2.5/weather?q=" +
      zip +
      "&appid=b1b35bba8b434a28a0be2a3e1071ae5b&units=imperial";
    fetch(URL).then(res => res.json()).then(json => {
      this.setState({ weatherData: json });
    });

    // const weater = PostServis.getPlace(currentPlace.zip)
    // weater.then(data => {
    //   setTimeout(() => {
    //     setWeatherData(data)

    //   }, 1000)
    // })


  }, []);

  console.log(weatherData);
  // console.log(weatherData.main);

  return (

    <div>
      {!weatherData ? <h1>Loading</h1> :
        <div>
          <Places place={currentPlace} newPlace={newPlace}></Places>
          <h1>Displaying weather for city {currentPlace.place}</h1>
          <p>текущий город... {currentPlace.place}</p>
          <p>текущий код города... {currentPlace.zip}</p>
          <p>{JSON.stringify(weatherData)}</p>
          <p>{weatherData.weater[0]}</p>
          <h1>
            {/* {weatherData.main} in {weatherData} */}
            {/* <img src={iconUrl} alt={weatherData.description} /> */}
          </h1>
          {/* <p>Current: {weatherData.main.temp}°</p>
          <p>High: {weatherData.main.temp_max}°</p>
          <p>Low: {weatherData.main.temp_min}°</p>
          <p>Wind Speed: {weatherData.wind.speed} mi/hr</p> */}

        </div>
      }
    </div>
  );
};

export default WeatherDisplay;
