import React, { useState } from "react";

let places = [
  { name: "Kyiv", zip: "703448" },
  { name: "Kharkiv", zip: "706483" },
  { name: "Odessa", zip: "698740" },
  { name: "Dnipro", zip: "709930" },
  { name: "Zaporizhia", zip: "687700" },
];

const Places = (props) => {
  return (
    <div>
      <h3>Select a city</h3>
      {places.map((place, index) => {
        return (
          <div>
            <button class='btn btn-outline-primary'
              key={index}
              onClick={() => {
                props.newPlace({ place: place.name, zip: place.zip });
              }}
            >
              {place.name}
            </button>
          </div>
        );
      })}
    </div>
  );
};

export default Places;
