import { PosAnimation } from 'leaflet'
import React, { useEffect, useState } from 'react'
import { MapContainer, Marker, Popup, TileLayer } from 'react-leaflet'
import './mymap.css'

const MyMap = (props) => {
    // console.log(props);
    // const position = [props.lat, props.lon]
    const [position, setPosition] = useState([props.lat, props.lon])


    useEffect(() => {
        setPosition([props.lat, props.lon])
        // const position = [props.lat, props.lon]

    }, [position])
    console.log(position);


    return (

        <div>
            <MapContainer center={position} zoom={9} scrollWheelZoom={false}>
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <Marker position={position}>
                    <Popup>
                        A pretty CSS3 popup. <br /> Easily customizable.
                    </Popup>
                </Marker>
            </MapContainer>
        </div>

    )
}

export default MyMap
