import "./App.css";
import WeatherDisplay from "./components/WeatherDisplay";
import Places from "./components/Places";
import { useEffect, useState } from "react";
import PostServis from "./components/PostService";

const [weatherData, setWeatherData] = useState();

useEffect(() => {

  const weater = PostServis.getPlace("703448")
  weater.then(data => {
    setTimeout(() => {
      setWeatherData(data)

    }, 1000)
  })


}, []);



function App() {
  return (
    <div className="App">
      <WeatherDisplay place={"Kiev"} zip={"703448"} data={weatherData} />
      <p>{weatherData.weather.main}</p>
    </div>
  );
}

export default App;
