import React, { useState } from "react";
import PostServis from "./PostService";
import Search from "./Search";

let places = [
  { name: "Kyiv", zip: "703448", lon: 30.5167, lat: 50.4333 },
  { name: "Kharkiv", zip: "706483" },
  { name: "Odessa", zip: "698740" },
  { name: "Dnipro", zip: "709930" },
  { name: "Zaporizhia", zip: "687700" },
  { name: "Lviv", zip: "702550" },
];

const Places = (props) => {

  const findTown = () => PostServis.getTownFind(props.place)
    .then((data) => {

      console.log(data);
      let towninfo = { place: data.name, zip: data.id, lon: data.coord.lon, lat: data.coord.lat }
      // props.props["newPlace"](towninfo)
      props.newPlace(towninfo)

    })

  return (
    <div className="sities">
      <h3>Select a city</h3>
      <nav className="bt-places">

        {places.map((place, index) => {
          return (
            <button className='btn btn-primary'
              key={index}
              onClick={(event) => {
                // props.newPlace({ place: place.name, zip: place.zip });
                props.newPlace(findTown(place.name))


              }}
            >
              {place.name}
            </button>
          );
        })}
      </nav>
      {/* <Search newPlace={props.newPlace} /> */}
    </div >
  );
};

export default Places;
