import React, { useState } from 'react'
import PostServis from './PostService';

const Search = (props) => {
    console.log(props);
    const [value, setValue] = useState("Enter city...");

    function submitHandler(event) {
        event.preventDefault();
        if (value.trim()) {
            console.log(value.trim());
            const findTown = PostServis.getTownFind(value.trim()).then(data => {
                console.log(data.name);
                console.log(data.id);
                props.newPlace({ place: data.name, zip: data.zip })
            })
            // props.newPlace(value);
            setValue("");
        }
    }

    return (
        <div>
            <form onSubmit={submitHandler}>
                <input value={value}
                    onFocus={(event) => (event.target.value = "")}
                    onChange={(event) => {
                        // console.log([event.target.value])
                        setValue(event.target.value)
                    }} />
                <button type="submit">Find city</button>
            </form>
        </div >
    )
}

export default Search
