import React, { useState } from 'react'
import PostServis from './PostService';

const Search = (props) => {
    // console.log(props.props["place"]);
    // console.log(props.props["newPlace"]);

    const [value, setValue] = useState("Enter city...");
    const [error, setError] = useState('Enter town for search')

    function submitHandler(event) {
        event.preventDefault();
        if (value.trim()) {
            console.log(value.trim());
            const findTown = PostServis.getTownFind(value.trim()).then(data => {

                console.log(data.name);
                console.log(data.id);
                let towninfo = { place: data.name, zip: data.id }
                // props.props["newPlace"](towninfo)
                props.newPlace(towninfo)

            })
            setValue("");
        }
    }

    return (
        <div>
            <form onSubmit={submitHandler}>
                <input value={value}
                    onFocus={(event) => (event.target.value = "")}
                    onChange={(event) => {
                        // console.log([event.target.value])
                        setValue(event.target.value)
                    }} />
                <button type="submit">Find city</button>
            </form>
            <p>{error}</p>
        </div >
    )
}

export default Search
