import React, { useState, useEffect } from "react";
import Places from "../Places";
import PostServis from "../PostService";
import classes from "./WeatherDisplay.module.css"
// import "bootstrap/dist/css/bootstrap.css";
// import { Navbar, NavItem, Nav, Grid, Row, Col } from "react-bootstrap";
import Icon from '../images/arrow_up.png';
import axios from "axios";
import Search from "../Search";
import MyMap from "../Map/MyMap";

const WeatherDisplay = (props) => {

  const [nowTime, setNowTime] = useState(getTime);
  const [currentPlace, setPlace] = useState(props);
  const [currentZip, setcurrentZip] = useState(props.zip);
  const [weatherData, setWeatherData] = useState()


  //---------------
  // const [peopleData, setPeopleData] = useState()

  // const getUsers = async () => {
  //   const users = await axios.get('https://randomuser.me/api/?page=1&results=1&nat=us');
  //   setPeopleData(users.data.results);
  // };

  // useEffect(() => {
  //   getUsers();
  // }, []);
  //-------------------
  //https://www.codegrepper.com/code-examples/javascript/async+axios+useeffect


  useEffect(() => {
    setNowTime(getTime)
    const w = PostServis.getPlace(currentZip).then(data => {
      setWeatherData(data)
    })

  }, [currentZip]);

  function getTime() {
    let dateNow = new Date();
    let optionsN = {
      weekday: 'long', year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit', hour12: false
    };
    dateNow = dateNow.toLocaleString('en-US', optionsN)
    // console.log(dateNow.toLocaleString('en-US', optionsN));
    return dateNow
  }

  function newPlace(newPlace) {
    setPlace(newPlace);
    setcurrentZip(newPlace.zip)
  }


  return (

    <div>
      {!weatherData ?
        <div className={classes.load}><h1>Loading......</h1></div> :
        <div>
          <header>
            <h3>Displaying weather for city <i><u>{currentPlace.place}</u></i></h3>
          </header>
          <Search newPlace={newPlace} />

          <div className="container" >
            <Places place={currentPlace} newPlace={newPlace} lon={lon} lat={lat}></Places>
            <div className="info-sity card border-primary mb-3">
              <p>{nowTime}</p>
              <h2>
                {weatherData.weather[0].main} in {weatherData.name}
                {<img src={"http://openweathermap.org/img/w/" + weatherData.weather[0].icon + ".png"} alt={weatherData.description} />}
              </h2>

              <p>Current Temp: {Math.round(weatherData.main.temp)}°</p>
              <div className={classes.flex}>
                {/* <p>High Temp: {Math.round(weatherData.main.temp_max)}°</p>
                <p>Low Temp: {Math.round(weatherData.main.temp_min)}°</p> */}
                <p>Pressure: {weatherData.main.pressure} hPa</p>
                <p>Humidity: {weatherData.main.humidity}% (влажность)</p>
              </div>


              <div className="wind ">
                <img style={{ width: 50, transform: `rotate(${weatherData.wind.deg}deg)` }} src={Icon} alt="direct" />
                <p>Wind Speed: {weatherData.wind.speed} m/s</p>
              </div>
            </div>
          </div>
          {/* -------------- */}
          <p>текущий город... {currentPlace.place}</p>
          <p>текущий код города... {currentPlace.zip}</p>
          <p>{weatherData.name}</p>
          <MyMap />
        </div>
      }
    </div >
  );
};

export default WeatherDisplay;
