import React, { useState } from "react";
import Search from "./Search";

let places = [
  { name: "Kyiv", zip: "703448", lon: 30.5167, lat: 50.4333 },
  { name: "Kharkiv", zip: "706483" },
  { name: "Odessa", zip: "698740" },
  { name: "Dnipro", zip: "709930" },
  { name: "Zaporizhia", zip: "687700" },
  { name: "Lviv", zip: "702550" },
];

const Places = (props) => {
  return (
    <div className="sities">
      <h3>Select a city</h3>
      <nav className="bt-places">

        {places.map((place, index) => {
          return (
            <button className='btn btn-primary'
              key={index}
              onClick={() => {
                props.newPlace({ place: place.name, zip: place.zip });
                console.log(Search());

              }}
            >
              {place.name}
            </button>
          );
        })}
      </nav>
      {/* <Search newPlace={props.newPlace} /> */}
    </div>
  );
};

export default Places;
