import React from "react";


// let places = [
//   { name: "Kyiv", country: "UA", zip: "703448", lon: 30.5167, lat: 50.4333 },
//   { name: "Kharkiv", country: "UA", zip: "706483", lon: 36.25, lat: 50 },
//   { name: "Odessa", country: "UA", zip: "698740", lon: 30.7326, lat: 46.4775 },
//   { name: "Dnipro", country: "UA", zip: "709930", lon: 34.9833, lat: 48.45 },
//   { name: "Zaporizhia", country: "UA", zip: "687700", lon: 35.1833, lat: 47.8167 },
//   { name: "Lviv", country: "UA", zip: "702550", lon: 24.0232, lat: 49.8383 },
// ];



const Places = (props) => {

  //передаем массив городов/мест и фильтруем по айди
  const removePlace = (places) => {
    //фильтр возвращает новый массив отфильтрованный по условию
    //по сути удалем из массива пост с айди переданным постом
    props.setNewPlaces(places.filter((p) => p.zip !== place.id));
  };

  return (
    <div className="sities">
      {/* <h3>Select a city</h3> */}
      <nav className="bt-places">
        {props.places.map((place, index) => {
          return (
            <div key={Math.random() + 'add'} className={place.add ? "addBlock" : ''}>
              <button className='btn btn-primary'
                key={index}
                onClick={(event) => {
                  props.updPosition([place.lat, place.lon])
                  props.newPlace({ name: place.name, zip: place.zip, country: place.country, lon: place.lon, lat: place.lat });
                  // const x = () => { MapTwo.tick([place.lat, place.lon]) }
                  // const x = new MapTwo().tick([place.lat, place.lon])
                }}
              >
                {place.name}

              </button>
              {place.add ?
                <button key={'del' + index} className="delPlace btn btn-danger"
                  onClick={() => {
                    // console.log(place);
                    removePlace(props.places.filter((p) => p.zip !== place.zip))
                    console.log(props.places.filter((p) => p.zip !== place.zip));

                  }}>del</button>
                : false}
            </div>

          );
        })}
      </nav>
      {/* <Search newPlace={props.newPlace} /> */}
    </div >
  );
};

export default Places;
