import React from "react";
import "./mymodal.css";


const MyModal = (props) => {
  return (
    <div className={props.activeModal ? "mymodal " : "mymodal close"} onClick={() => props.setActiveModal(false)}>
      <div className="modal-body" onClick={(e) => e.stopPropagation()}>
        <button type="button" className="btn-close" onClick={() => props.setActiveModal(false)}></button>
        <div className="alert alert-dismissible alert-warning">
          <p>Api Погоды</p>
          <p>https://openweathermap.org/city/698740</p>
        </div>
        <button onClick={() => props.setActiveModal(false)}>
          Close modal
        </button>
      </div>
    </div>

  )
}


export default MyModal

